[{
  "name": "Create_loopback_interface_unified",
  "description": "Create loopback interface - OPENCONFIG, UNIFIED, INTERFACES, LOOPBACK",
  "version": 1,
  "tasks": [
  	{
      "name": "UNIFIED_write_structured_device_data",
      "taskReferenceName": "UNIFIED_write_structured_device_data",
      "type": "SIMPLE",
      "inputParameters": {
        "id": "${workflow.input.id}",
		"uri": "/frinx-openconfig-interfaces:interfaces/interface/Loopback${workflow.input.loopback_id}",
		"template": "{\"interface\":[{\"name\":\"Loopback${workflow.input.loopback_id}\",\"config\":{\"type\":\"iana-if-type:softwareLoopback\",\"enabled\":false,\"name\":\"Loopback${workflow.input.loopback_id}\"}}]}",
		"params": "{}"
      }
    }
  ],
  "outputParameters": {
    "response_body": "${UNIFIED_write_structured_device_data.output.response_body}"
  },
  "schemaVersion": 2
}]

