[{
  "name": "Read_interfaces_all_from_unified",
  "description": "read interfaces configuration for all devices in unified topology - OPENCONFIG, UNICONFIG, INTERFACES, LOOPBACK",
  "version": 1,
  "tasks": [
  	{
      "name": "sub_read_unified",
      "taskReferenceName": "read_unified",
	  "inputParameters": {
        "task": "Read_interfaces_unified"
      },
      "type": "SUB_WORKFLOW",
      "subWorkflowParam": {
        "name": "Get_all_devices_in_unified_as_tasks",
        "version": 1
      }
    },
  	{
      "name": "sub_read_components",
      "taskReferenceName": "read_ifcs",
	  "inputParameters": {
        "dynamic_tasks": "${read_unified.output.dynamic_tasks}",
        "dynamic_tasks_i": "${read_unified.output.dynamic_tasks_i}"
      },
      "type": "SUB_WORKFLOW",
      "subWorkflowParam": {
        "name": "Dynamic_fork",
        "version": 1
      }
    }
  ],
  "outputParameters": {
  	"interfaces": "${read_ifcs.output}"
  },
  "schemaVersion": 2
}]

[{
  "name": "Create_loopback_all_in_unified",
  "description": "Create loopback interface for all devices in unified topology - OPENCONFIG, UNIFIED, INTERFACES, LOOPBACK",
  "version": 1,
  "tasks": [
  	{
      "name": "sub_read_unified",
      "taskReferenceName": "read_unified",
	  "inputParameters": {
        "task": "Create_loopback_interface_unified",
        "task_params": "{\"loopback_id\": \"${workflow.input.loopback_id}\"}"
      },
      "type": "SUB_WORKFLOW",
      "subWorkflowParam": {
        "name": "Get_all_devices_in_unified_as_tasks",
        "version": 1
      }
    },
  	{
      "name": "sub_create_loopback",
      "taskReferenceName": "create_loopback",
	  "inputParameters": {
        "dynamic_tasks": "${read_unified.output.dynamic_tasks}",
        "dynamic_tasks_i": "${read_unified.output.dynamic_tasks_i}"
      },
      "type": "SUB_WORKFLOW",
      "subWorkflowParam": {
        "name": "Dynamic_fork",
        "version": 1
      }
    }
  ],
  "outputParameters": {
  	"interfaces": "${create_loopback.output}"
  },
  "schemaVersion": 2
}]

[{
  "name": "Create_loopback_all_in_uniconfig",
