[{
  "name": "Execute_and_read_rpc_cli_device_from_inventory",
  "description": "execute commands loaded from inventory for a CLI device - SOUTHBOUND, CLI, EXECUTEANDREAD, INVENTORY",
  "version": 1,
  "tasks": [
    {
      "name": "sub_read_command",
      "taskReferenceName": "read_command",
		"inputParameters": {
        "id": "${workflow.input.command_id}"
      },
      "type": "SUB_WORKFLOW",
      "subWorkflowParam": {
        "name": "Get_cli_show_command_from_inventory",
        "version": 1
      }
    },
    {
      "name": "sub_execute",
      "taskReferenceName": "execute_template",
	  "inputParameters": {
        "id": "${workflow.input.id}",
        "template": "${read_command.output.command}",
		"params": "${workflow.input.params}"
      },
      "type": "SUB_WORKFLOW",
      "subWorkflowParam": {
        "name": "Execute_and_read_rpc_cli_device",
        "version": 1
      }
    }
  ],
  "outputParameters": {
    "output": "${execute_template.output.output}"
  },
  "schemaVersion": 2
}]

[{
  "name": "Execute_and_read_rpc_cli_device_from_inventory_update_inventory",
  "description": "execute commands loaded from inventory for a CLI device and store the output in inventory - SOUTHBOUND, CLI, EXECUTEANDREAD, INVENTORY",
  "version": 1,
  "tasks": [
    {
      "name": "sub_execute",
      "taskReferenceName": "execute_template",
	  "inputParameters": {
        "id": "${workflow.input.id}",
		"command_id": "${workflow.input.command_id}",
		"params": "${workflow.input.params}"
      },
      "type": "SUB_WORKFLOW",
      "subWorkflowParam": {
        "name": "Execute_and_read_rpc_cli_device_from_inventory",
        "version": 1
      }
    },
    {
      "name": "sub_store_in_inventory",
      "taskReferenceName": "save",
	  "inputParameters": {
        "id": "${workflow.input.id}",
        "field": "${workflow.input.command_id}",
        "value": "${execute_template.output.output}"
      },
      "type": "SUB_WORKFLOW",
      "subWorkflowParam": {
        "name": "Add_field_to_device_in_inventory",
        "version": 1
      }
    }
  ],
  "outputParameters": {
    "output": "${execute_template.output.output}"
  },
  "schemaVersion": 2
}]

