	  	"uri": "/frinx-openconfig-network-instance:network-instances/network-instance/default/protocols/protocol/frinx-openconfig-policy-types:BGP/default/bgp/neighbors/neighbor/$neighbor",
	  	"template": "{\n\t\"neighbor\": [\n\t\t{\n\t\t\"neighbor-address\"  : \"$neighbor\",\n\t\t  \"config\": {\n\t\t    \"neighbor-address\": \"$neighbor\",\n \t\t    \"description\": \"^:tier2:bgp:int:uplink:vlan::rusgtdc-ccx01:eth1/1:$vlan:trusted:vif-fgtbvmlf\",\n\t\t    \"enabled\": true,\n\t\t    \"peer-as\": \"$as\"\n\t\t  },\n\t\t  \"transport\": {\n\t\t    \"config\": {\n\t\t     \"local-address\": \"Vlan$vlan\"\n\t\t   }\n\t\t  },\n\t\t  \"afi-safis\": {\n\t\t\t\"afi-safi\": [\n                {\n                    \"afi-safi-name\": \"frinx-openconfig-bgp-types:IPV4_UNICAST\",\n                    \"config\": {\n                        \"afi-safi-name\": \"frinx-openconfig-bgp-types:IPV4_UNICAST\"\n                    }\n                }\n            ]\n\t\t  },\n\t\t  \"apply-policy\": {\n\t\t  \t\"config\": {\n\t\t  \t\t\"export-policy\": [\"NRP_DEFAULT_ONLY\"] \n\t\t  \t}\n\t\t  }\n\t\t}\n\t]\n}",
	    "params": "{\"vlan\": \"1111\", \"as\": \"65071\", \"neighbor\":\"${terraform_apply.output.output.nat_public_ips.value[0]}\", \"rpol\":\"NRP_DEFAULT_ONLY\"}"
      },
      "type": "SUB_WORKFLOW",
      "subWorkflowParam": {
